// 30-Day Content Schedule Generator with Cost Optimization
const input = $input.first().json;

// Input processing and defaults
const topic = input.topic || 'inner child healing';
const niche = input.niche || 'trauma healing';
const platforms = input.platforms || ['pinterest', 'instagram', 'linkedin'];
const contentTypes = input.contentTypes || ['quote', 'infographic', 'exercise', 'worksheet', 'affirmation'];
const monthlyBudget = input.monthlyBudget || 30;
const startDate = input.startDate || new Date().toISOString().split('T')[0];
const audience = input.audience || 'trauma survivors seeking healing';

// Cost optimization settings
const MODEL_COSTS = {
  'gpt-4o-mini': { input: 0.15, output: 0.60 }, // per 1M tokens
  'gpt-3.5-turbo': { input: 0.50, output: 1.50 },
  'gpt-4': { input: 30.00, output: 60.00 }
};

const SELECTED_MODEL = 'gpt-4o-mini'; // Most cost-effective

// Platform-specific settings
const PLATFORM_SETTINGS = {
  pinterest: {
    format: '2:3 (1000x1500px)',
    optimalTimes: {
      weekday: '8:00 PM EST',
      weekend: '2:00 PM EST'
    },
    contentFocus: 'visual storytelling',
    hashtagCount: 10,
    descriptionLength: 500,
    keywordDensity: 'high'
  },
  instagram: {
    format: '1:1 (1080x1080px)',
    optimalTimes: {
      weekday: '12:00 PM EST',
      weekend: '1:00 PM EST'
    },
    contentFocus: 'emotional connection',
    hashtagCount: 15,
    descriptionLength: 250,
    keywordDensity: 'medium'
  },
  linkedin: {
    format: '1.91:1 (1200x630px)',
    optimalTimes: {
      weekday: '9:00 AM EST',
      weekend: 'skip'
    },
    contentFocus: 'professional insight',
    hashtagCount: 5,
    descriptionLength: 300,
    keywordDensity: 'low'
  },
  facebook: {
    format: '1.91:1 (1200x630px)',
    optimalTimes: {
      weekday: '3:00 PM EST',
      weekend: '12:00 PM EST'
    },
    contentFocus: 'community building',
    hashtagCount: 8,
    descriptionLength: 400,
    keywordDensity: 'medium'
  }
};

// Content type templates for trauma healing niche
const CONTENT_TEMPLATES = {
  quote: {
    structure: 'Inspirational quote + context + call-to-action',
    examples: ['healing affirmations', 'recovery mantras', 'self-compassion quotes'],
    visualElements: ['calming colors', 'soft typography', 'nature backgrounds'],
    engagement: 'high',
    costEfficiency: 'high'
  },
  infographic: {
    structure: 'Educational data + visual hierarchy + actionable tips',
    examples: ['trauma symptoms guide', 'healing timeline', 'coping strategies'],
    visualElements: ['charts', 'icons', 'step-by-step layout'],
    engagement: 'very high',
    costEfficiency: 'medium'
  },
  exercise: {
    structure: 'Guided activity + instructions + benefits',
    examples: ['breathing techniques', 'grounding exercises', 'self-reflection prompts'],
    visualElements: ['step illustrations', 'progress trackers', 'calming imagery'],
    engagement: 'high',
    costEfficiency: 'high'
  },
  worksheet: {
    structure: 'Interactive template + prompts + guidance',
    examples: ['emotion mapping', 'trigger identification', 'progress tracking'],
    visualElements: ['form layouts', 'writing spaces', 'instructional text'],
    engagement: 'very high',
    costEfficiency: 'low'
  },
  affirmation: {
    structure: 'Positive statement + explanation + practice guide',
    examples: ['self-worth affirmations', 'safety affirmations', 'growth mindset'],
    visualElements: ['beautiful typography', 'soft backgrounds', 'meaningful symbols'],
    engagement: 'high',
    costEfficiency: 'very high'
  },
  meditation: {
    structure: 'Guided practice + duration + benefits',
    examples: ['body scan', 'loving-kindness', 'inner child connection'],
    visualElements: ['peaceful imagery', 'timer elements', 'instruction cards'],
    engagement: 'medium',
    costEfficiency: 'medium'
  }
};

// Generate 30-day schedule
function generate30DaySchedule(startDate, platforms, contentTypes, topic) {
  const schedule = [];
  const start = new Date(startDate);

  for (let day = 0; day < 30; day++) {
    const currentDate = new Date(start);
    currentDate.setDate(start.getDate() + day);

    const dayOfWeek = currentDate.getDay();
    const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;
    const dayName = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][dayOfWeek];

    // Smart platform rotation (skip LinkedIn on weekends)
    let availablePlatforms = [...platforms];
    if (isWeekend && availablePlatforms.includes('linkedin')) {
      availablePlatforms = availablePlatforms.filter(p => p !== 'linkedin');
    }

    const platform = availablePlatforms[day % availablePlatforms.length];
    const contentType = contentTypes[day % contentTypes.length];

    // Get platform settings
    const platformSettings = PLATFORM_SETTINGS[platform];
    const contentTemplate = CONTENT_TEMPLATES[contentType];

    // Determine optimal posting time
    const optimalTime = isWeekend ? 
      platformSettings.optimalTimes.weekend : 
      platformSettings.optimalTimes.weekday;

    // Skip if platform doesn't post on weekends
    if (optimalTime === 'skip') {
      continue;
    }

    // Generate content topic variation
    const topicVariations = [
      `${topic} - ${contentType}`,
      `${contentType} for ${topic}`,
      `Daily ${contentType}: ${topic}`,
      `${topic} journey: ${contentType}`,
      `Healing through ${contentType}`,
      `${contentType} practice for ${topic}`
    ];

    const topicVariation = topicVariations[day % topicVariations.length];

    // Priority system
    let priority = 'medium';
    if (day < 7) priority = 'high';              // First week
    else if (day < 14) priority = 'high';        // Second week
    else if (day < 21) priority = 'medium';      // Third week
    else priority = 'low';                       // Fourth week

    // Content difficulty and engagement prediction
    const engagementScore = {
      'very high': 95,
      'high': 85,
      'medium': 70,
      'low': 50
    }[contentTemplate.engagement];

    schedule.push({
      day: day + 1,
      date: currentDate.toISOString().split('T')[0],
      dayName: dayName,
      platform: platform,
      contentType: contentType,
      topic: topicVariation,
      optimalTime: optimalTime,
      priority: priority,

      // Platform specifications
      imageFormat: platformSettings.format,
      contentFocus: platformSettings.contentFocus,
      hashtagCount: platformSettings.hashtagCount,
      descriptionLength: platformSettings.descriptionLength,

      // Content specifications
      structure: contentTemplate.structure,
      visualElements: contentTemplate.visualElements,
      expectedEngagement: engagementScore,

      // SEO and optimization
      keywordDensity: platformSettings.keywordDensity,
      seoFocus: `${topic} ${contentType} ${platform}`,

      // Production notes
      estimatedCreationTime: getCreationTime(contentType),
      difficultyLevel: getDifficultyLevel(contentType),
      costEfficiency: contentTemplate.costEfficiency,

      // Engagement strategy
      callToAction: generateCTA(contentType, platform),
      hashtagStrategy: generateHashtagStrategy(topic, contentType, platform),

      // Analytics tracking
      trackingGoals: ['engagement', 'reach', 'saves', 'shares'],
      kpiTargets: {
        engagement: `${engagementScore}%`,
        reach: '1000+',
        saves: '50+',
        comments: '10+'
      }
    });
  }

  return schedule;
}

// Helper functions
function getCreationTime(contentType) {
  const times = {
    quote: '15 minutes',
    affirmation: '10 minutes',
    exercise: '25 minutes',
    infographic: '45 minutes',
    worksheet: '60 minutes',
    meditation: '30 minutes'
  };
  return times[contentType] || '20 minutes';
}

function getDifficultyLevel(contentType) {
  const difficulty = {
    quote: 'Easy',
    affirmation: 'Easy',
    exercise: 'Medium',
    infographic: 'Hard',
    worksheet: 'Hard',
    meditation: 'Medium'
  };
  return difficulty[contentType] || 'Medium';
}

function generateCTA(contentType, platform) {
  const ctas = {
    pinterest: {
      quote: 'Save this for daily inspiration üìå',
      exercise: 'Try this healing exercise today üíö',
      infographic: 'Pin this guide for reference üìö',
      worksheet: 'Download and start your healing journey üìù',
      affirmation: 'Save and repeat daily üåü',
      meditation: 'Practice this meditation tonight üßò‚Äç‚ôÄÔ∏è'
    },
    instagram: {
      quote: 'Double tap if this resonates üíï',
      exercise: 'Try this and share your experience! üåà',
      infographic: 'Save this post for later reference üíæ',
      worksheet: 'Comment if you want the full worksheet! üìã',
      affirmation: 'Repeat this affirmation with me ‚ú®',
      meditation: 'Practice this tonight and let me know how it goes üåô'
    },
    linkedin: {
      quote: 'What resonates with you most?',
      exercise: 'How do you practice self-care at work?',
      infographic: 'Which tip will you implement first?',
      worksheet: 'Share your thoughts on trauma-informed workplaces',
      affirmation: 'How do you build resilience professionally?',
      meditation: 'Do you practice mindfulness at work?'
    }
  };

  return ctas[platform]?.[contentType] || 'Share your thoughts below üí¨';
}

function generateHashtagStrategy(topic, contentType, platform) {
  const baseHashtags = {
    pinterest: ['#TraumaHealing', '#InnerChildWork', '#MentalHealthAwareness', '#SelfCare', '#HealingJourney'],
    instagram: ['#TraumaRecovery', '#HealingCommunity', '#MentalHealthMatters', '#SelfLove', '#InnerChildHealing'],
    linkedin: ['#MentalHealthAtWork', '#TraumaInformed', '#WorkplaceWellness', '#Resilience', '#ProfessionalGrowth']
  };

  const contentHashtags = {
    quote: ['#Inspiration', '#Motivation', '#Quotes', '#Wisdom'],
    exercise: ['#SelfCareExercise', '#Mindfulness', '#Wellness', '#Practice'],
    infographic: ['#Education', '#Awareness', '#Guide', '#Tips'],
    worksheet: ['#SelfReflection', '#Growth', '#Journaling', '#Tool'],
    affirmation: ['#Affirmations', '#PositiveVibes', '#Mindset', '#Empowerment'],
    meditation: ['#Meditation', '#Mindfulness', '#Peace', '#Relaxation']
  };

  const platformBase = baseHashtags[platform] || baseHashtags.instagram;
  const contentSpecific = contentHashtags[contentType] || [];

  return [...platformBase, ...contentSpecific].slice(0, PLATFORM_SETTINGS[platform].hashtagCount);
}

// Cost calculation
function calculateScheduleCost(schedule, model = SELECTED_MODEL) {
  const tokensPerPost = 200; // Conservative estimate for content generation
  const totalTokens = schedule.length * tokensPerPost;
  const costs = MODEL_COSTS[model];

  const inputCost = (totalTokens / 1000000) * costs.input;
  const outputCost = (totalTokens / 1000000) * costs.output;
  const totalCost = inputCost + outputCost;

  return {
    totalPosts: schedule.length,
    estimatedTokens: totalTokens,
    inputCost: inputCost.toFixed(4),
    outputCost: outputCost.toFixed(4),
    totalCost: totalCost.toFixed(4),
    costPerPost: (totalCost / schedule.length).toFixed(4),
    model: model
  };
}

// Generate the schedule
const contentSchedule = generate30DaySchedule(startDate, platforms, contentTypes, topic);
const costAnalysis = calculateScheduleCost(contentSchedule);

// Budget validation
const withinBudget = parseFloat(costAnalysis.totalCost) <= monthlyBudget;
const budgetUtilization = (parseFloat(costAnalysis.totalCost) / monthlyBudget * 100).toFixed(1);

// Analytics and insights
const platformDistribution = {};
const contentTypeDistribution = {};
const weeklyBreakdown = [];

contentSchedule.forEach(post => {
  // Platform distribution
  platformDistribution[post.platform] = (platformDistribution[post.platform] || 0) + 1;

  // Content type distribution
  contentTypeDistribution[post.contentType] = (contentTypeDistribution[post.contentType] || 0) + 1;

  // Weekly breakdown
  const weekNumber = Math.ceil(post.day / 7);
  if (!weeklyBreakdown[weekNumber - 1]) {
    weeklyBreakdown[weekNumber - 1] = {
      week: weekNumber,
      posts: 0,
      platforms: new Set(),
      contentTypes: new Set(),
      priorities: { high: 0, medium: 0, low: 0 }
    };
  }

  weeklyBreakdown[weekNumber - 1].posts++;
  weeklyBreakdown[weekNumber - 1].platforms.add(post.platform);
  weeklyBreakdown[weekNumber - 1].contentTypes.add(post.contentType);
  weeklyBreakdown[weekNumber - 1].priorities[post.priority]++;
});

// Convert Sets to Arrays for JSON serialization
weeklyBreakdown.forEach(week => {
  week.platforms = Array.from(week.platforms);
  week.contentTypes = Array.from(week.contentTypes);
});

const result = {
  success: true,
  metadata: {
    topic: topic,
    niche: niche,
    audience: audience,
    startDate: startDate,
    generatedAt: new Date().toISOString(),
    totalDays: 30,
    totalPosts: contentSchedule.length,
    platforms: platforms,
    contentTypes: contentTypes
  },

  costAnalysis: {
    ...costAnalysis,
    monthlyBudget: monthlyBudget,
    withinBudget: withinBudget,
    budgetUtilization: `${budgetUtilization}%`,
    remainingBudget: (monthlyBudget - parseFloat(costAnalysis.totalCost)).toFixed(2),
    costEfficiencyRating: withinBudget ? 'Excellent' : 'Over Budget'
  },

  schedule: contentSchedule,

  analytics: {
    platformDistribution: platformDistribution,
    contentTypeDistribution: contentTypeDistribution,
    weeklyBreakdown: weeklyBreakdown,
    averageEngagementScore: (contentSchedule.reduce((sum, post) => sum + post.expectedEngagement, 0) / contentSchedule.length).toFixed(1),
    totalEstimatedCreationTime: calculateTotalCreationTime(contentSchedule),
    difficultyDistribution: getDifficultyDistribution(contentSchedule)
  },

  optimizationTips: [
    'Batch create similar content types to save time',
    'Use templates for consistent branding',
    'Schedule posts during optimal times',
    'Monitor engagement and adjust strategy',
    'Repurpose high-performing content across platforms',
    'Plan content themes weekly for coherence'
  ],

  nextSteps: [
    'Review the complete 30-day schedule',
    'Create content templates for each type',
    'Set up social media scheduling tools',
    'Track performance metrics',
    'Adjust posting times based on audience engagement',
    'Prepare backup content for low-performing posts'
  ]
};

// Helper functions for analytics
function calculateTotalCreationTime(schedule) {
  const timeInMinutes = schedule.reduce((total, post) => {
    const time = parseInt(post.estimatedCreationTime.split(' ')[0]);
    return total + time;
  }, 0);

  const hours = Math.floor(timeInMinutes / 60);
  const minutes = timeInMinutes % 60;

  return `${hours}h ${minutes}m`;
}

function getDifficultyDistribution(schedule) {
  const distribution = { Easy: 0, Medium: 0, Hard: 0 };
  schedule.forEach(post => {
    distribution[post.difficultyLevel]++;
  });
  return distribution;
}

return [{ json: result }];

// Process and combine all schedule data with AI enhancements
const scheduleData = $('30-Day Schedule Engine').first().json;
const contentEnhancement = $('Content Detail Enhancer').first().json.choices[0].message.content;
const hashtagStrategies = $('Hashtag Strategy Generator').first().json.choices[0].message.content;

// Parse content enhancements
const enhancedContent = {};
const contentLines = contentEnhancement.split('\n').filter(line => line.trim());

contentLines.forEach(line => {
  if (line.match(/^Day \d+:/)) {
    const dayMatch = line.match(/^Day (\d+):(.+)/);
    if (dayMatch) {
      const dayNum = parseInt(dayMatch[1]);
      const content = dayMatch[2].trim();
      enhancedContent[dayNum] = content;
    }
  }
});

// Parse hashtag strategies
const hashtagSets = {};
const hashtagLines = hashtagStrategies.split('\n').filter(line => line.trim());

hashtagLines.forEach(line => {
  const match = line.match(/^([^-]+)\s*-\s*Set\s*(\d+):\s*(.+)/);
  if (match) {
    const platform = match[1].trim().toLowerCase();
    const setNumber = parseInt(match[2]);
    const hashtags = match[3].trim();

    if (!hashtagSets[platform]) {
      hashtagSets[platform] = {};
    }
    hashtagSets[platform][setNumber] = hashtags;
  }
});

// Enhance schedule with AI-generated content
const enhancedSchedule = scheduleData.schedule.map(post => {
  const enhancement = enhancedContent[post.day] || `Create ${post.contentType} about ${post.topic}`;

  // Assign hashtag set (rotate through available sets)
  const platformHashtags = hashtagSets[post.platform] || {};
  const hashtagSetKeys = Object.keys(platformHashtags);
  const setIndex = ((post.day - 1) % hashtagSetKeys.length) + 1;
  const assignedHashtags = platformHashtags[setIndex] || post.hashtagStrategy.join(' ');

  return {
    ...post,
    contentIdea: enhancement,
    optimizedHashtags: assignedHashtags,

    // Add production checklist
    productionChecklist: {
      research: `Research ${post.topic} for authentic insights`,
      design: `Create ${post.imageFormat} image with ${post.visualElements.join(', ')}`,
      copy: `Write ${post.descriptionLength} char description with ${post.contentFocus} focus`,
      hashtags: `Use optimized hashtag set: ${assignedHashtags}`,
      schedule: `Post at ${post.optimalTime} on ${post.date}`,
      engage: `Respond to comments within 2 hours`,
      analyze: `Track ${post.kpiTargets.engagement} engagement rate`
    },

    // Content brief
    contentBrief: {
      objective: `Create ${post.contentType} that resonates with ${scheduleData.metadata.audience}`,
      keyMessage: post.topic,
      visualStyle: post.visualElements.join(', '),
      tone: getToneForContent(post.contentType),
      mustInclude: [`${post.contentFocus}`, 'trauma-informed language', 'hope and healing'],
      avoid: ['triggering language', 'medical advice', 'overpromising quick fixes']
    },

    // Performance predictions
    predictions: {
      bestTimeToPost: post.optimalTime,
      expectedReach: post.kpiTargets.reach,
      engagementRate: post.kpiTargets.engagement,
      contentLifespan: getContentLifespan(post.platform, post.contentType),
      viralPotential: getViralPotential(post.contentType, post.expectedEngagement)
    }
  };
});

// Helper functions
function getToneForContent(contentType) {
  const tones = {
    quote: 'Inspirational and empowering',
    affirmation: 'Gentle and nurturing', 
    exercise: 'Instructional and supportive',
    infographic: 'Educational and clear',
    worksheet: 'Structured and helpful',
    meditation: 'Calming and peaceful'
  };
  return tones[contentType] || 'Supportive and authentic';
}

function getContentLifespan(platform, contentType) {
  const lifespans = {
    pinterest: { quote: '6 months', infographic: '1 year', exercise: '3 months' },
    instagram: { quote: '24 hours', infographic: '3 days', exercise: '48 hours' },
    linkedin: { quote: '48 hours', infographic: '1 week', exercise: '3 days' }
  };
  return lifespans[platform]?.[contentType] || '1 week';
}

function getViralPotential(contentType, engagement) {
  if (engagement > 90 && ['quote', 'infographic'].includes(contentType)) return 'High';
  if (engagement > 80) return 'Medium';
  return 'Low';
}

// Calculate weekly summaries
const weeklySummaries = [];
for (let week = 1; week <= 4; week++) {
  const weekStart = (week - 1) * 7 + 1;
  const weekEnd = Math.min(week * 7, enhancedSchedule.length);
  const weekPosts = enhancedSchedule.slice(weekStart - 1, weekEnd);

  weeklySummaries.push({
    week: week,
    dateRange: `${weekPosts[0].date} to ${weekPosts[weekPosts.length - 1].date}`,
    postCount: weekPosts.length,
    platforms: [...new Set(weekPosts.map(p => p.platform))],
    contentTypes: [...new Set(weekPosts.map(p => p.contentType))],
    totalCreationTime: weekPosts.reduce((sum, p) => sum + parseInt(p.estimatedCreationTime), 0),
    highPriorityPosts: weekPosts.filter(p => p.priority === 'high').length,
    avgEngagementPrediction: (weekPosts.reduce((sum, p) => sum + p.expectedEngagement, 0) / weekPosts.length).toFixed(1),
    focus: getWeeklyFocus(week),
    goals: getWeeklyGoals(week)
  });
}

function getWeeklyFocus(week) {
  const focuses = {
    1: 'Introduction and awareness building',
    2: 'Deep healing and practical tools', 
    3: 'Community building and engagement',
    4: 'Integration and future planning'
  };
  return focuses[week];
}

function getWeeklyGoals(week) {
  const goals = {
    1: ['Build initial audience', 'Establish expertise', 'Create engagement'],
    2: ['Provide valuable resources', 'Increase saves/shares', 'Build trust'],
    3: ['Foster community interaction', 'Encourage user-generated content', 'Build relationships'],
    4: ['Showcase transformation', 'Plan future content', 'Gather feedback']
  };
  return goals[week];
}

// Generate content creation timeline
const creationTimeline = enhancedSchedule.map(post => ({
  day: post.day,
  date: post.date,
  contentType: post.contentType,
  platform: post.platform,
  creationDeadline: getCreationDeadline(post.date),
  estimatedTime: post.estimatedCreationTime,
  difficulty: post.difficultyLevel,
  dependencies: getContentDependencies(post.contentType),
  tools: getRequiredTools(post.contentType, post.platform)
}));

function getCreationDeadline(postDate) {
  const date = new Date(postDate);
  date.setDate(date.getDate() - 1); // Create 1 day before posting
  return date.toISOString().split('T')[0];
}

function getContentDependencies(contentType) {
  const deps = {
    quote: ['Source quote', 'Background image'],
    infographic: ['Research data', 'Visual elements', 'Icons'],
    exercise: ['Instructions', 'Step illustrations'],
    worksheet: ['Template design', 'Content prompts'],
    affirmation: ['Affirmation text', 'Typography'],
    meditation: ['Script', 'Audio (optional)', 'Visual guide']
  };
  return deps[contentType] || ['Content', 'Visual'];
}

function getRequiredTools(contentType, platform) {
  const baseTools = ['Canva/Figma', 'Image editing'];
  const specific = {
    infographic: ['Data visualization', 'Icon library'],
    worksheet: ['PDF creation', 'Form design'],
    meditation: ['Audio editing (optional)']
  };
  return [...baseTools, ...(specific[contentType] || [])];
}

// Generate final comprehensive report
const finalResult = {
  success: true,
  summary: {
    title: '30-Day Content Schedule with AI Enhancement',
    topic: scheduleData.metadata.topic,
    audience: scheduleData.metadata.audience,
    totalPosts: enhancedSchedule.length,
    totalCost: scheduleData.costAnalysis.totalCost,
    costPerPost: scheduleData.costAnalysis.costPerPost,
    budgetStatus: scheduleData.costAnalysis.withinBudget ? '‚úÖ Within Budget' : '‚ùå Over Budget',
    estimatedROI: 'High (trauma healing niche has strong engagement)',
    generatedAt: new Date().toISOString()
  },

  costAnalysis: scheduleData.costAnalysis,

  schedule: enhancedSchedule,

  weeklySummaries: weeklySummaries,

  creationTimeline: creationTimeline,

  hashtagStrategies: hashtagSets,

  analytics: scheduleData.analytics,

  actionPlan: {
    preparation: [
      'Set up content creation tools (Canva, scheduling platform)',
      'Create brand templates for each content type',
      'Research trauma healing keywords for each week',
      'Set up analytics tracking for all platforms'
    ],
    execution: [
      'Batch create Week 1 content (2-3 hours)',
      'Schedule first week of posts',
      'Monitor engagement and adjust timing',
      'Prepare Week 2 content based on performance'
    ],
    optimization: [
      'Track which content types perform best',
      'Adjust posting times based on audience activity',
      'Expand on high-performing topics',
      'Build email list from engaged followers'
    ]
  },

  recommendations: {
    immediate: [
      'Start with highest-priority posts (Week 1)',
      'Create 3-5 post templates for efficiency', 
      'Set up automated hashtag rotation',
      'Schedule posts 1 week in advance'
    ],
    ongoing: [
      'Monitor trauma healing trends and adjust content',
      'Engage with community posts for 30 min daily',
      'Share user success stories (with permission)',
      'Create monthly themes for content cohesion'
    ],
    advanced: [
      'Develop signature content series',
      'Launch email course for lead generation',
      'Partner with other trauma healing creators',
      'Create premium content offerings'
    ]
  },

  nextSteps: [
    '1. Review and approve the 30-day schedule',
    '2. Set up content creation workspace and tools',
    '3. Create first week of content (start with easy posts)',
    '4. Schedule posts and monitor engagement',
    '5. Adjust strategy based on performance data',
    '6. Plan month 2 content based on successful themes'
  ]
};

return [{ json: finalResult }];

// Budget exceeded handler
const scheduleData = $('30-Day Schedule Engine').first().json;

const budgetSolutions = {
  error: 'Monthly budget exceeded',
  currentBudget: scheduleData.costAnalysis.monthlyBudget,
  estimatedCost: scheduleData.costAnalysis.totalCost,
  overage: (parseFloat(scheduleData.costAnalysis.totalCost) - scheduleData.costAnalysis.monthlyBudget).toFixed(2),

  solutions: {
    reduceFrequency: {
      description: 'Post every other day instead of daily',
      newPostCount: Math.floor(scheduleData.schedule.length / 2),
      estimatedCost: (parseFloat(scheduleData.costAnalysis.totalCost) / 2).toFixed(2),
      savings: '50%'
    },
    focusHighROI: {
      description: 'Focus on high-engagement content types only',
      recommendedTypes: ['quote', 'affirmation', 'exercise'],
      estimatedSavings: '40%'
    },
    weeklyBatches: {
      description: 'Generate content weekly instead of monthly',
      weeklyBudget: (scheduleData.costAnalysis.monthlyBudget / 4).toFixed(2),
      postsPerWeek: Math.floor(scheduleData.schedule.length / 4)
    },
    manualCreation: {
      description: 'Use AI for planning only, create content manually',
      estimatedCost: (parseFloat(scheduleData.costAnalysis.totalCost) * 0.1).toFixed(2),
      savings: '90%'
    }
  },

