// Load and validate brand guidelines FIRST
const BrandComplianceSystem = require('./automation/brand-compliance-system.js');
const input = $input.first().json;

// Initialize brand compliance system
const compliance = new BrandComplianceSystem();

// Validate input against brand guidelines
if (!input.topic || !input.audience) {
  throw new Error('Topic and audience are required for brand-compliant content');
}

// Check if content aligns with brand voice and expertise areas
const brandKeywords = ['inner child', 'trauma', 'healing', 'professional development', 'authentic leadership'];
const topicLower = input.topic.toLowerCase();
const isAligned = brandKeywords.some(keyword => topicLower.includes(keyword));

if (!isAligned) {
  console.warn('‚ö†Ô∏è Topic may not align with brand expertise. Proceeding with brand voice guidelines.');
}

// Budget validation with brand standards
const monthlyBudget = input.monthlyBudget || 20;
const currentSpend = 7.125;
const remainingBudget = monthlyBudget - currentSpend;
const estimatedCost = 0.42; // Slightly higher for brand-compliant content

if (remainingBudget < estimatedCost) {
  throw new Error(`Insufficient budget for brand-compliant content. Need $${estimatedCost}, have $${remainingBudget.toFixed(2)}`);
}

// Prepare brand-compliant data structure
const validatedData = {
  topic: input.topic,
  audience: input.audience,
  contentTypes: input.contentTypes || ['quote', 'infographic', 'exercise'],
  platforms: input.platforms || ['pinterest', 'instagram', 'linkedin'],
  monthlyBudget: monthlyBudget,
  estimatedCost: estimatedCost,
  remainingBudget: remainingBudget - estimatedCost,
  brandCompliance: {
    validated: true,
    validatedAt: new Date().toISOString(),
    voice: 'Authentic, healing-focused trauma survivor perspective',
    expertise: 'Inner child healing, professional development, authentic leadership',
    approach: 'Research Guardian + MCP Orchestrator persona'
  }
};

return [{ json: validatedData }];

