// Generate brand-compliant 30-day schedule
const BrandComplianceSystem = require('./automation/brand-compliance-system.js');
const input = $input.first().json;
const compliance = new BrandComplianceSystem();

// Get brand-compliant schedule template
const template = compliance.generateContentTemplate('schedule');

const schedule = {
  totalDays: 30,
  platforms: ['pinterest', 'instagram', 'linkedin'],
  contentTypes: ['quote', 'infographic', 'exercise'],
  brandConsistency: {
    voiceTone: 'Maintain authentic trauma survivor perspective across all platforms',
    keywordStrategy: 'Rotate between inner child healing, trauma recovery, and professional development',
    engagementApproach: 'Always validate, never dismiss, provide practical value',
    expertiseAreas: ['Inner child healing', 'Trauma recovery', 'Professional development', 'Authentic leadership']
  },
  schedule: []
};

// Brand-compliant content themes for 30 days
const healingThemes = [
  'Recognition and validation',
  'Self-compassion practices',
  'Creating emotional safety',
  'Professional healing integration',
  'Authentic leadership development',
  'Boundary setting and maintenance',
  'Trauma-informed communication',
  'Inner child reparenting',
  'Somatic healing practices',
  'Building secure relationships'
];

// Generate daily schedule with brand voice
for (let day = 1; day <= 30; day++) {
  const platform = schedule.platforms[(day - 1) % schedule.platforms.length];
  const contentType = schedule.contentTypes[(day - 1) % schedule.contentTypes.length];
  const theme = healingThemes[(day - 1) % healingThemes.length];

  let optimalTime;
  if (platform === 'pinterest') optimalTime = '8:00 PM EST';
  else if (platform === 'instagram') optimalTime = '12:00 PM EST';
  else optimalTime = '9:00 AM EST';

  // Platform-specific brand voice adaptation
  let brandVoiceNote;
  if (platform === 'pinterest') {
    brandVoiceNote = 'Searchable, valuable content with personal healing insights';
  } else if (platform === 'instagram') {
    brandVoiceNote = 'Personal, vulnerable sharing with practical applications';
  } else {
    brandVoiceNote = 'Professional healing insights connecting personal growth to business success';
  }

  schedule.schedule.push({
    day: `Day ${day}`,
    platform: platform,
    contentType: contentType,
    optimalTime: optimalTime,
    topic: input.topic,
    healingTheme: theme,
    brandVoice: {
      perspective: 'Trauma survivor who became a healing professional',
      tone: 'Authentic, empathetic, growth-oriented',
      approach: brandVoiceNote
    },
    complianceNote: 'Content must validate experience, provide practical value, and maintain healing focus'
  });
}

// Add brand compliance validation
const validation = compliance.validateContent(schedule, 'schedule');
schedule.complianceScore = validation.score;
schedule.brandAlignment = validation.brandAlignment;

return [{ json: schedule }];

