// Generate files with brand compliance documentation
const BrandComplianceSystem = require('./automation/brand-compliance-system.js');
const input = $input.first().json;
const fs = require('fs');
const path = require('path');

const compliance = new BrandComplianceSystem();

// Create brand-compliant output directory
const outputDir = 'temp/brand-compliant-outputs';
if (!fs.existsSync(outputDir)) {
  fs.mkdirSync(outputDir, { recursive: true });
}

// Save carousel content with brand notes
const carouselFile = path.join(outputDir, 'brand-compliant-carousel.json');
fs.writeFileSync(carouselFile, JSON.stringify(input.deliverables.carousel, null, 2));

// Save schedule with brand guidelines
const scheduleFile = path.join(outputDir, 'brand-compliant-30-day-schedule.json');
fs.writeFileSync(scheduleFile, JSON.stringify(input.deliverables.schedule, null, 2));

// Save social optimization
const socialFile = path.join(outputDir, 'brand-compliant-social-posts.json');
fs.writeFileSync(socialFile, JSON.stringify(input.deliverables.socialOptimization, null, 2));

// Save image prompts
const imageFile = path.join(outputDir, 'brand-compliant-image-prompts.json');
fs.writeFileSync(imageFile, JSON.stringify(input.deliverables.imagePrompts, null, 2));

// Save individual brand-compliant slides
const slidesDir = path.join(outputDir, 'brand-compliant-slides');
if (!fs.existsSync(slidesDir)) {
  fs.mkdirSync(slidesDir, { recursive: true });
}

input.deliverables.carousel.slides.forEach((slide, index) => {
  const slideFile = path.join(slidesDir, `brand-slide-${index + 1}.json`);
  fs.writeFileSync(slideFile, JSON.stringify(slide, null, 2));
});

// Generate comprehensive brand compliance report
const brandReport = {
  success: true,
  brandCompliance: input.brandCompliance,
  deliverables: input.deliverables,
  complianceScores: input.complianceScores,
  qualityAssurance: input.qualityAssurance,
  brandGuidelines: {
    voice: 'Authentic trauma survivor perspective with professional expertise',
    approach: 'Research Guardian + MCP Orchestrator persona',
    expertise: ['Inner child healing', 'Trauma recovery', 'Professional development', 'Authentic leadership'],
    platforms: ['Pinterest', 'Instagram', 'LinkedIn'],
    contentTypes: ['Quote', 'Infographic', 'Exercise'],
    keyPrinciples: [
      'Lead with empathy and personal experience',
      'Validate trauma experiences',
      'Provide practical, accessible guidance',
      'Connect personal healing to professional growth',
      'Maintain authentic, non-clinical voice',
      'Focus on hope and healing, not just pain'
    ]
  },
  costAnalysis: {
    tokensUsed: 625,
    estimatedCost: input.budget.estimatedCost,
    budgetRemaining: input.budget.remainingBudget,
    efficiency: 'High - brand-compliant content with authentic voice integration',
    valueDelivered: 'Complete brand-aligned content suite with compliance validation'
  },
  recommendations: input.brandRecommendations || [],
  timestamp: new Date().toISOString()
};

const reportFile = path.join(outputDir, 'brand-compliance-report.json');
fs.writeFileSync(reportFile, JSON.stringify(brandReport, null, 2));

// Save brand guidelines reference
const guidelinesFile = path.join(outputDir, 'brand-guidelines-reference.json');
const guidelinesRef = {
  description: 'Brand guidelines extracted from .cursorrules and chudi_humanized.mdc',
  voice: input.brandCompliance.voice,
  approach: input.brandCompliance.approach,
  expertise: input.brandCompliance.expertise,
  extractedAt: input.brandCompliance.validatedAt,
  complianceScore: input.finalComplianceScore,
  sourceFiles: ['.cursorrules', '.cursorrules.json', 'chudi_humanized.mdc'],
  usage: 'Reference these guidelines for all future content creation'
};
fs.writeFileSync(guidelinesFile, JSON.stringify(guidelinesRef, null, 2));

// Add file paths to output
const output = {
  ...input,
  files: {
    carousel: carouselFile,
    schedule: scheduleFile,
    socialPosts: socialFile,
    imagePrompts: imageFile,
    slides: slidesDir,
    complianceReport: reportFile,
    brandGuidelines: guidelinesFile
  },
  brandComplianceValidated: true,
  finalScore: input.finalComplianceScore
};

return [{ json: output }];
