// Combined 30-Day Content Planner with Visual Enhancement Engine
const input = $('Content Planning Webhook').first().json;

// Input validation and optimization
const topic = input.topic || 'trauma healing and inner child work';
const audience = input.audience || 'trauma survivors seeking healing';
const platforms = input.platforms || ['pinterest', 'instagram', 'linkedin'];
const contentTypes = input.contentTypes || ['carousel', 'quote', 'exercise', 'worksheet', 'affirmation'];
const monthlyBudget = parseFloat(input.monthlyBudget) || 50;
const dailyPostCount = parseInt(input.dailyPostCount) || 1;
const focusAreas = input.focusAreas || ['inner child healing', 'trauma recovery', 'self-compassion', 'emotional regulation'];

// Platform specifications for visual guidance
const platformSpecs = {
  pinterest: {
    format: '2:3 ratio (1000x1500px)',
    textOverlay: 'Large, readable text with high contrast',
    colors: 'Soft pastels, earth tones, calming blues',
    engagement: 'Save-worthy content, DIY elements',
    keywords: ['healing', 'selflove', 'traumarecovery', 'innerchild'],
    optimalTime: '8:00 PM EST'
  },
  instagram: {
    format: '1:1 square (1080x1080px) for feed, 9:16 for stories',
    textOverlay: 'Bold, mobile-friendly fonts',
    colors: 'Bright, vibrant colors with brand consistency',
    engagement: 'Questions, polls, interactive elements',
    keywords: ['mentalhealthawareness', 'healing', 'selfcare', 'traumainformed'],
    optimalTime: '12:00 PM EST'
  },
  linkedin: {
    format: '1.91:1 landscape (1200x628px)',
    textOverlay: 'Professional, clean design',
    colors: 'Professional blues, whites, minimal accent colors',
    engagement: 'Educational content, professional insights',
    keywords: ['mentalhealthatwork', 'traumainformedcare', 'wellness', 'leadership'],
    optimalTime: '9:00 AM EST'
  },
  facebook: {
    format: '1.91:1 landscape (1200x630px)',
    textOverlay: 'Clear, family-friendly fonts',
    colors: 'Warm, welcoming colors',
    engagement: 'Community building, sharing experiences',
    keywords: ['familyhealing', 'traumarecovery', 'parentinghealing', 'community'],
    optimalTime: '3:00 PM EST'
  }
};

// Content type specifications for visual guidance
const contentTypeSpecs = {
  carousel: {
    slideCount: '5-10 slides',
    visualStyle: 'Consistent theme across slides',
    textStrategy: 'Progressive storytelling',
    engagement: 'High - encourages swipe-through'
  },
  quote: {
    slideCount: '1 slide',
    visualStyle: 'Text-focused with decorative elements',
    textStrategy: 'Inspirational, shareable',
    engagement: 'Save and share focused'
  },
  exercise: {
    slideCount: '3-5 slides',
    visualStyle: 'Step-by-step visual progression',
    textStrategy: 'Clear instructions, actionable',
    engagement: 'Try-it-yourself motivation'
  },
  worksheet: {
    slideCount: '4-6 slides',
    visualStyle: 'Clean, printable design',
    textStrategy: 'Educational, guided reflection',
    engagement: 'Download and complete'
  },
  affirmation: {
    slideCount: '1-3 slides',
    visualStyle: 'Calming, supportive imagery',
    textStrategy: 'Positive, empowering language',
    engagement: 'Daily practice encouragement'
  }
};

// Budget analysis and optimization
const estimatedTokensPerPost = 800; // Conservative estimate for combined generation
const gptMiniCostPer1KTokens = 0.75; // $0.15 input + $0.60 output per 1K tokens
const totalDays = 30;
const totalPosts = totalDays * dailyPostCount;
const estimatedTotalTokens = totalPosts * estimatedTokensPerPost;
const estimatedMonthlyCost = (estimatedTotalTokens / 1000) * gptMiniCostPer1KTokens;

const budgetAnalysis = {
  monthlyBudget: monthlyBudget,
  estimatedCost: estimatedMonthlyCost.toFixed(2),
  withinBudget: estimatedMonthlyCost <= monthlyBudget,
  costPerPost: (estimatedMonthlyCost / totalPosts).toFixed(3),
  overage: estimatedMonthlyCost > monthlyBudget ? (estimatedMonthlyCost - monthlyBudget).toFixed(2) : 0
};

// Generate 30-day content strategy with weekly themes
const weeklyThemes = {
  week1: {
    theme: 'Understanding Trauma',
    focus: 'Education and awareness',
    platforms: platforms.slice(0, 2),
    contentTypes: ['carousel', 'quote', 'exercise']
  },
  week2: {
    theme: 'Inner Child Connection',
    focus: 'Self-discovery and compassion',
    platforms: platforms,
    contentTypes: ['worksheet', 'affirmation', 'carousel']
  },
  week3: {
    theme: 'Healing Practices',
    focus: 'Practical tools and techniques',
    platforms: platforms.slice(0, 2),
    contentTypes: ['exercise', 'carousel', 'quote']
  },
  week4: {
    theme: 'Integration and Growth',
    focus: 'Moving forward with healing',
    platforms: platforms,
    contentTypes: ['affirmation', 'worksheet', 'carousel']
  }
};

// Generate optimized batch configuration
const batchConfig = {
  totalBatches: budgetAnalysis.withinBudget ? 4 : 2,
  postsPerBatch: budgetAnalysis.withinBudget ? Math.ceil(totalPosts / 4) : Math.ceil(totalPosts / 2),
  batchFrequency: budgetAnalysis.withinBudget ? 'weekly' : 'bi-weekly',
  priorityContent: ['carousel', 'worksheet', 'exercise']
};

const result = {
  planningConfig: {
    topic: topic,
    audience: audience,
    platforms: platforms,
    contentTypes: contentTypes,
    focusAreas: focusAreas,
    duration: '30 days',
    dailyPostCount: dailyPostCount,
    totalPosts: totalPosts
  },

  budgetAnalysis: budgetAnalysis,

  weeklyThemes: weeklyThemes,

  batchConfiguration: batchConfig,

  platformSpecs: platformSpecs,

  contentTypeSpecs: contentTypeSpecs,

  optimizationSettings: {
    useGPT4Mini: true,
    structuredOutput: true,
    batchProcessing: true,
    visualEnhancement: true,
    traumaInformed: true,
    maxTokensPerGeneration: 1200,
    temperature: 0.3
  },

  generationInstructions: {
    contentGeneration: 'Create detailed content with trauma-informed language',
    visualGuidance: 'Provide specific visual descriptions for each piece',
    copyEnhancement: 'Include engaging copy suggestions and CTAs',
    platformOptimization: 'Optimize for each platform\'s best practices',
    engagementStrategy: 'Include community-building elements'
  },

  timestamp: new Date().toISOString()
};

return [{ json: result }];

// Process and combine all AI outputs into comprehensive content plan
const planningData = $('Advanced Planning & Budget Engine').first().json;
const scheduleRaw = $('30-Day Schedule Generator').first().json.choices[0].message.content;
const visualGuidanceRaw = $('Visual & Copy Enhancement Engine').first().json.choices[0].message.content;
const strategyRaw = $('Content Strategy Optimizer').first().json.choices[0].message.content;

// Parse 30-day schedule
const schedule = [];
const scheduleLines = scheduleRaw.split('\n').filter(line => line.trim() && line.includes('Day'));

scheduleLines.forEach(line => {
  const dayMatch = line.match(/Day (\d+):\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+?)\s*\|\s*(.+)/);
  if (dayMatch) {
    schedule.push({
      day: parseInt(dayMatch[1]),
      platform: dayMatch[2].trim(),
      contentType: dayMatch[3].trim(),
      topic: dayMatch[4].trim(),
      optimalTime: dayMatch[5].trim(),
      engagementHook: dayMatch[6].trim(),
      week: Math.ceil(parseInt(dayMatch[1]) / 7),
      theme: planningData.weeklyThemes[`week${Math.ceil(parseInt(dayMatch[1]) / 7)}`]?.theme || 'General Healing'
    });
  }
});

// Parse visual guidance (first 7 days detailed)
const visualGuidance = {};
const guidanceLines = visualGuidanceRaw.split('\n');
let currentDay = null;
let currentSection = null;

guidanceLines.forEach(line => {
  const dayMatch = line.match(/Day (\d+)\s*-\s*(.+):/); 
  if (dayMatch) {
    currentDay = parseInt(dayMatch[1]);
    visualGuidance[currentDay] = {
      topic: dayMatch[2].trim(),
      visual: '',
      copyOptions: [],
      cta: '',
      engagement: '',
      hashtags: [],
      production: ''
    };
  } else if (currentDay && line.includes('ðŸŽ¨ Visual:')) {
    visualGuidance[currentDay].visual = line.replace('ðŸŽ¨ Visual:', '').trim();
  } else if (currentDay && line.includes('ðŸ“ Copy Options:')) {
    const copyText = line.replace('ðŸ“ Copy Options:', '').trim();
    visualGuidance[currentDay].copyOptions = copyText.split(',').map(opt => opt.trim());
  } else if (currentDay && line.includes('ðŸŽ¯ CTA:')) {
    visualGuidance[currentDay].cta = line.replace('ðŸŽ¯ CTA:', '').trim();
  } else if (currentDay && line.includes('ðŸ”¥ Engagement:')) {
    visualGuidance[currentDay].engagement = line.replace('ðŸ”¥ Engagement:', '').trim();
  } else if (currentDay && line.includes('#ï¸âƒ£ Hashtags:')) {
    const hashtagText = line.replace('#ï¸âƒ£ Hashtags:', '').trim();
    visualGuidance[currentDay].hashtags = hashtagText.split(',').map(tag => tag.trim());
  } else if (currentDay && line.includes('ðŸ“‹ Production:')) {
    visualGuidance[currentDay].production = line.replace('ðŸ“‹ Production:', '').trim();
  }
});

// Enhance schedule with visual guidance
const enhancedSchedule = schedule.map(day => {
  const guidance = visualGuidance[day.day] || {};
  return {
    ...day,
    visualDescription: guidance.visual || `Follow ${planningData.platformSpecs[day.platform]?.colors} color scheme`,
    copyOptions: guidance.copyOptions || ['Engaging text overlay needed'],
    callToAction: guidance.cta || 'Engage with this post',
    engagementStrategy: guidance.engagement || 'Encourage comments and shares',
    hashtags: guidance.hashtags || planningData.platformSpecs[day.platform]?.keywords || [],
    productionNotes: guidance.production || 'Standard platform formatting',
    platformSpecs: planningData.platformSpecs[day.platform] || {},
    contentSpecs: planningData.contentTypeSpecs[day.contentType] || {},
    estimatedCreationTime: day.contentType === 'carousel' ? '45-60 minutes' : 
                          day.contentType === 'worksheet' ? '30-45 minutes' : '15-30 minutes',
    priority: day.day <= 7 ? 'High' : day.day <= 14 ? 'Medium' : 'Standard',
    productionReady: day.day <= 7 ? true : false
  };
});

// Cost tracking
const tokenUsage = {
  scheduleGeneration: $('30-Day Schedule Generator').first().json.usage?.total_tokens || 0,
  visualEnhancement: $('Visual & Copy Enhancement Engine').first().json.usage?.total_tokens || 0,
  strategyOptimization: $('Content Strategy Optimizer').first().json.usage?.total_tokens || 0
};

const totalTokens = Object.values(tokenUsage).reduce((sum, tokens) => sum + tokens, 0);
const actualCost = (totalTokens / 1000) * 0.75; // GPT-4o-mini pricing

// Generate weekly content batches
const weeklyBatches = {};
for (let week = 1; week <= 4; week++) {
  const weekContent = enhancedSchedule.filter(day => day.week === week);
  weeklyBatches[`week${week}`] = {
    theme: planningData.weeklyThemes[`week${week}`].theme,
    focus: planningData.weeklyThemes[`week${week}`].focus,
    contentCount: weekContent.length,
    platforms: [...new Set(weekContent.map(c => c.platform))],
    contentTypes: [...new Set(weekContent.map(c => c.contentType))],
    estimatedCreationTime: weekContent.reduce((total, content) => {
      const time = parseInt(content.estimatedCreationTime.split('-')[0]);
      return total + time;
    }, 0) + ' minutes',
    priorityContent: weekContent.filter(c => c.priority === 'High'),
    content: weekContent
  };
}

// Generate production timeline
const productionTimeline = {
  week1: {
    preparation: 'Days 1-2: Gather brand assets, create templates',
    production: 'Days 3-5: Create priority content (carousels, worksheets)',
    refinement: 'Days 6-7: Review, optimize, and schedule',
    estimatedHours: '8-12 hours total'
  },
  week2: {
    preparation: 'Day 8: Review week 1 performance, adjust strategy',
    production: 'Days 9-12: Batch create week 2 content',
    refinement: 'Days 13-14: Polish and schedule',
    estimatedHours: '6-10 hours total'
  },
  ongoing: {
    pattern: 'Batch create 1 week ahead, review and optimize weekly',
    maintenance: '2-3 hours weekly after initial setup',
    optimization: 'Monthly strategy review and adjustment'
  }
};

const result = {
  success: true,

  planSummary: {
    topic: planningData.planningConfig.topic,
    totalPosts: enhancedSchedule.length,
    platforms: [...new Set(enhancedSchedule.map(s => s.platform))],
    contentTypes: [...new Set(enhancedSchedule.map(s => s.contentType))],
    duration: '30 days',
    readyToProduceCount: enhancedSchedule.filter(s => s.productionReady).length
  },

  costAnalysis: {
    plannedBudget: planningData.budgetAnalysis.monthlyBudget,
    actualPlanningCost: actualCost.toFixed(4),
    estimatedTotalCost: planningData.budgetAnalysis.estimatedCost,
    budgetRemaining: (planningData.budgetAnalysis.monthlyBudget - actualCost).toFixed(2),
    costPerPost: (parseFloat(planningData.budgetAnalysis.estimatedCost) / enhancedSchedule.length).toFixed(3),
    efficiency: 'Excellent - combined planning with visual guidance',
    tokenBreakdown: tokenUsage
  },

  contentCalendar: {
    schedule: enhancedSchedule,
    weeklyBatches: weeklyBatches,
    productionTimeline: productionTimeline
  },

  visualCreationGuide: {
    platformSpecs: planningData.platformSpecs,
    contentTypeSpecs: planningData.contentTypeSpecs,
    detailedGuidance: visualGuidance,
    generalGuidelines: {
      traumaInformed: [
        'Use calming, safe imagery',
        'Avoid triggering content',
        'Include content warnings when needed',
        'Focus on empowerment and hope'
      ],
      accessibility: [
        'High contrast text',
        'Readable fonts (minimum 16px)',
        'Alt text for images',
        'Color-blind friendly palettes'
      ],
      brandConsistency: [
        'Consistent color palette',
        'Recognizable font choices',
        'Logo placement standards',
        'Voice and tone guidelines'
      ]
    }
  },

  engagementStrategy: {
    communityBuilding: [
      'Ask meaningful questions',
      'Share personal experiences appropriately',
      'Create safe spaces for discussion',
      'Respond authentically to comments'
    ],
    contentOptimization: [
      'Post at optimal times for each platform',
      'Use platform-specific hashtags',
      'Cross-promote between platforms',
      'Analyze performance and adapt'
    ],
    growthTactics: [
      'Collaborate with other trauma-informed creators',
      'Share user-generated content',
      'Create shareable resources',
      'Host live sessions or workshops'
    ]
  },

  strategyInsights: strategyRaw,

  nextSteps: [
    '1. Review and approve the 30-day content plan',
    '2. Gather visual assets and brand materials',
    '3. Start with Week 1 priority content creation',
    '4. Set up content scheduling tools',
    '5. Create tracking system for engagement metrics',
    '6. Establish weekly review and optimization process'
  ],

  qualityAssurance: {
    traumaInformedCheck: 'All content reviewed for safety and appropriateness',
    platformOptimization: 'Each post optimized for target platform',
    engagementFocus: 'CTAs and hooks designed for community building',
    visualGuidance: 'Detailed creative direction provided',
    strategicAlignment: 'Content supports overall healing and growth mission'
  },

  timestamp: new Date().toISOString()
};

return [{ json: result }];

// Budget exceeded - provide optimization recommendations
const planningData = $('Advanced Planning & Budget Engine').first().json;

const recommendations = {
  error: 'Budget exceeded for full 30-day plan',
  currentBudget: planningData.budgetAnalysis.monthlyBudget,
  estimatedCost: planningData.budgetAnalysis.estimatedCost,
  overage: planningData.budgetAnalysis.overage,

  budgetOptimizedOptions: {
    option1: {
      name: '14-Day Focused Plan',
      description: 'Create 14 days of high-priority content',
      estimatedCost: (parseFloat(planningData.budgetAnalysis.estimatedCost) * 0.47).toFixed(2),
      contentReduction: 'Focus on carousels and worksheets only',
      platforms: planningData.planningConfig.platforms.slice(0, 2)
    },
    option2: {
      name: 'Weekly Batch Plan',
      description: 'Create one week at a time, 4 separate runs',
      estimatedCost: (parseFloat(planningData.budgetAnalysis.estimatedCost) * 0.25).toFixed(2),
      contentReduction: '7-8 posts per batch',
      platforms: planningData.planningConfig.platforms
    },
    option3: {
      name: 'Template-Based Plan',
      description: 'Create 10 template designs, reuse with different content',
      estimatedCost: (parseFloat(planningData.budgetAnalysis.estimatedCost) * 0.33).toFixed(2),
      contentReduction: 'Template approach reduces custom creation',
      platforms: planningData.planningConfig.platforms
    }
  },

  freeAlternatives: [
    'Use ChatGPT web interface for content ideation',
    'Create content calendar in Google Sheets',
    'Use Canva free templates for visual consistency',
    'Manual hashtag research using platform tools',
    'Weekly planning sessions instead of monthly'
  ],

  costSavingStrategies: [
    'Implement content templates to reduce generation needs',
    'Focus on highest-performing content types',
    'Create evergreen content that can be reused',
    'Use user-generated content when appropriate',
    'Batch creation to reduce setup costs'
  ],

  quickStart: {
    action: 'Generate Week 1 priority content only',
    cost: (parseFloat(planningData.budgetAnalysis.estimatedCost) * 0.25).toFixed(2),
    includes: [
      '7 days of detailed content',
      'Visual specifications for each post',
      'Engagement strategies',
      'Platform-optimized formatting'
    ],
    withinBudget: (parseFloat(planningData.budgetAnalysis.estimatedCost) * 0.25) <= planningData.budgetAnalysis.monthlyBudget
  },

  timestamp: new Date().toISOString()
};

return [{ json: recommendations }];
