// Input validation and budget check
const input = $input.first().json;

// Validate required fields
if (!input.topic || !input.audience) {
  throw new Error('Topic and audience are required');
}

// Budget validation
const monthlyBudget = input.monthlyBudget || 20;
const currentSpend = 7.125; // Current spend tracking
const remainingBudget = monthlyBudget - currentSpend;

if (remainingBudget < 0.5) {
  throw new Error(`Budget exceeded. Remaining: $${remainingBudget.toFixed(2)}`);
}

// Cost estimation for this request
const estimatedCost = 0.32; // Estimated cost for content generation

if (remainingBudget < estimatedCost) {
  throw new Error(`Insufficient budget. Need $${estimatedCost}, have $${remainingBudget.toFixed(2)}`);
}

// Prepare validated data
