// Generate 30-day posting schedule
const input = $input.first().json;

const schedule = {
  totalDays: 30,
  platforms: ['pinterest', 'instagram', 'linkedin'],
  contentTypes: ['quote', 'infographic', 'exercise'],
  schedule: []
};

// Generate daily schedule
for (let day = 1; day <= 30; day++) {
  const platform = schedule.platforms[(day - 1) % schedule.platforms.length];
  const contentType = schedule.contentTypes[(day - 1) % schedule.contentTypes.length];

  let optimalTime;
  if (platform === 'pinterest') optimalTime = '8:00 PM EST';
  else if (platform === 'instagram') optimalTime = '12:00 PM EST';
  else optimalTime = '9:00 AM EST';

  schedule.schedule.push({
    day: `Day ${day}`,
    platform: platform,
    contentType: contentType,
    optimalTime: optimalTime,
    topic: input.topic
  });
}

return [{ json: schedule }];

// Combine carousel and schedule data
