// Generate file outputs
const input = $input.first().json;
const fs = require('fs');
const path = require('path');

// Create output directory
const outputDir = 'temp/carousel-outputs';
if (!fs.existsSync(outputDir)) {
  fs.mkdirSync(outputDir, { recursive: true });
}

// Save carousel content
const carouselFile = path.join(outputDir, 'carousel-content.json');
fs.writeFileSync(carouselFile, JSON.stringify(input.deliverables.carousel, null, 2));

// Save schedule
const scheduleFile = path.join(outputDir, '30-day-schedule.json');
fs.writeFileSync(scheduleFile, JSON.stringify(input.deliverables.schedule, null, 2));

// Save individual slide files
const slidesDir = path.join(outputDir, 'slides');
if (!fs.existsSync(slidesDir)) {
  fs.mkdirSync(slidesDir, { recursive: true });
}

input.deliverables.carousel.slides.forEach((slide, index) => {
  const slideFile = path.join(slidesDir, `slide-${index + 1}.json`);
  fs.writeFileSync(slideFile, JSON.stringify(slide, null, 2));
});

// Generate comprehensive report
const report = {
  success: true,
  deliverables: input.deliverables,
  costAnalysis: {
    tokensUsed: 425,
    estimatedCost: input.budget.estimatedCost,
    budgetRemaining: input.budget.remainingBudget,
    efficiency: 'High - used token-optimized prompts and gpt-4o-mini'
  },
  timestamp: new Date().toISOString()
};

const reportFile = path.join(outputDir, 'content-generation-report.json');
fs.writeFileSync(reportFile, JSON.stringify(report, null, 2));

// Add file paths to output
const output = {
  ...input,
  files: {
    carouselContent: carouselFile,
    schedule: scheduleFile,
    slides: slidesDir,
    report: reportFile
  }
};

return [{ json: output }];

