// Inner Child Healing Pinterest SEO Optimizer
const input = $input.first().json;
const content = input.content || '';
const contentType = input.type || 'general';
const ageGroup = input.ageGroup || 'adult'; // adult, teen, child

// Research-based Inner Child Healing keywords
const innerChildKeywords = {
  primary: ['#InnerChildHealing', '#InnerChildWork', '#ChildhoodTrauma', '#TraumaHealing', '#HealingJourney'],
  emotional: ['#InnerChildMeditation', '#InnerChildTherapy', '#EmotionalHealing', '#ChildhoodWounds', '#HealingTheChild'],
  therapeutic: ['#InnerChildExercises', '#ChildhoodRecovery', '#TraumaRecovery', '#TherapyTools', '#SelfCompassion'],
  growth: ['#PersonalGrowth', '#EmotionalWellness', '#MentalHealthHealing', '#SelfLove', '#HealingAffirmations'],
  specific: ['#AdultChildrenOfAlcoholics', '#NarcissisticAbuse', '#EmotionalNeglect', '#AttachmentHealing', '#ReparentingYourself']
};

// Inner Child specific content optimization
const innerChildOptimization = {
  meditation: {
    title: `Inner Child Meditation: ${content}`,
    description: `üßò‚Äç‚ôÄÔ∏è Gentle meditation to connect with and heal your inner child. Perfect for trauma recovery and emotional healing. ${innerChildKeywords.primary.slice(0,2).join(' ')} ${innerChildKeywords.emotional.slice(0,2).join(' ')}`,
    hashtags: [...innerChildKeywords.primary.slice(0,2), ...innerChildKeywords.emotional.slice(0,3), '#Meditation', '#SelfHealing']
  },
  affirmation: {
    title: `Inner Child Affirmation: ${content.substring(0, 50)}...`,
    description: `üíï Loving affirmation for your inner child. Speak these words of healing and self-compassion to nurture the child within. ${innerChildKeywords.primary.slice(0,3).join(' ')} ${innerChildKeywords.growth.slice(0,2).join(' ')}`,
    hashtags: [...innerChildKeywords.primary.slice(0,3), ...innerChildKeywords.growth.slice(0,2), '#HealingAffirmations', '#SelfLove']
  },
  exercise: {
    title: `Inner Child Exercise: ${content}`,
    description: `üé® Healing exercise to reconnect with your inner child. Step-by-step guide for trauma recovery and emotional healing. ${innerChildKeywords.therapeutic.slice(0,2).join(' ')} ${innerChildKeywords.emotional.slice(0,2).join(' ')}`,
    hashtags: [...innerChildKeywords.therapeutic.slice(0,2), ...innerChildKeywords.emotional.slice(0,2), '#HealingExercises', '#TraumaTherapy', '#InnerWork']
  },
  worksheet: {
    title: `Free Inner Child Healing Worksheet: ${content}`,
    description: `üìù Download this free inner child healing worksheet. Perfect for therapy, self-reflection, and childhood trauma recovery. ${innerChildKeywords.primary.slice(0,2).join(' ')} ${innerChildKeywords.therapeutic.slice(0,2).join(' ')}`,
    hashtags: [...innerChildKeywords.primary.slice(0,2), ...innerChildKeywords.therapeutic.slice(0,2), '#FreeWorksheet', '#TherapyTools', '#ChildhoodHealing']
  },
  journaling: {
    title: `Inner Child Journal Prompts: ${content}`,
    description: `‚úçÔ∏è Healing journal prompts to connect with your inner child. Explore childhood memories, emotions, and healing. ${innerChildKeywords.primary.slice(0,2).join(' ')} ${innerChildKeywords.growth.slice(0,2).join(' ')}`,
    hashtags: [...innerChildKeywords.primary.slice(0,2), ...innerChildKeywords.growth.slice(0,2), '#JournalPrompts', '#HealingJourney', '#SelfReflection']
  },
  quote: {
    title: `Inner Child Healing Quote: ${content.substring(0, 40)}...`,
    description: `üíñ Gentle reminder for your inner child healing journey. Save this loving message for when you need comfort and encouragement. ${innerChildKeywords.primary.slice(0,3).join(' ')}`,
    hashtags: [...innerChildKeywords.primary.slice(0,3), ...innerChildKeywords.growth.slice(0,2), '#HealingQuotes', '#InnerChildLove']
  },
  reparenting: {
    title: `Reparenting Guide: ${content}`,
    description: `üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Learn to reparent your inner child with love and compassion. Healing childhood wounds through self-nurturing. ${innerChildKeywords.specific.slice(-1).join(' ')} ${innerChildKeywords.emotional.slice(0,2).join(' ')}`,
    hashtags: [...innerChildKeywords.specific.slice(-1), ...innerChildKeywords.emotional.slice(0,2), '#Reparenting', '#SelfNurturing', '#HealingTheChild']
  },
  general: {
    title: `Inner Child Healing: ${content}`,
    description: `üåü Support for your inner child healing journey. Gentle resources for childhood trauma recovery and emotional wellness. ${innerChildKeywords.primary.slice(0,3).join(' ')}`,
    hashtags: [...innerChildKeywords.primary.slice(0,3), ...innerChildKeywords.emotional.slice(0,2), '#InnerChildSupport']
  }
};

// Select optimization based on content type
const optimization = innerChildOptimization[contentType] || innerChildOptimization.general;

// Inner Child specific Pinterest optimization
const innerChildPinterestOptimized = {
  title: optimization.title,
  description: optimization.description,
  hashtags: optimization.hashtags,

  // Pinterest-specific formatting
  pinTitle: optimization.title.substring(0, 100),
  pinDescription: optimization.description.substring(0, 500),

  // Inner Child specific content suggestions
  contentSuggestions: {
    textOverlay: contentType === 'quote' || contentType === 'affirmation' ? content : `INNER CHILD: ${content.substring(0, 25)}...`,
    boardSuggestions: [
      'Inner Child Healing Journey',
      'Childhood Trauma Recovery', 
      'Reparenting Your Inner Child',
      'Inner Child Meditation',
      'Healing Childhood Wounds',
      'Inner Child Affirmations',
      'Emotional Healing & Growth'
    ],
    bestTimes: ['Tuesday-Thursday 8-11pm EST', 'Sunday 2-5pm EST'], // When people seek emotional content
    imageSpecs: {
      ratio: '2:3 (1000x1500px)',
      style: 'Soft pastels (pink, lavender, mint), childlike elements (teddy bears, butterflies, flowers), nurturing imagery',
      elements: 'Include symbols of childhood, healing hands, hearts, gentle nature scenes, avoid dark or triggering imagery',
      fonts: 'Gentle, readable fonts - avoid harsh or aggressive styling'
    }
  },

  // Inner Child SEO optimization
  seo: {
    primaryKeywords: innerChildKeywords.primary,
    emotionalKeywords: innerChildKeywords.emotional,
    therapeuticKeywords: innerChildKeywords.therapeutic,
    longTailKeywords: [
      'inner child healing meditation',
      'how to heal your inner child',
      'inner child therapy exercises',
      'reparenting your inner child',
      'childhood trauma healing affirmations',
      'inner child healing for adults',
      'healing childhood emotional wounds'
    ]
  },

  // Age-appropriate content suggestions
  ageSpecificGuidance: {
    adult: 'Focus on reparenting, therapy tools, meditation',
    teen: 'Emphasize self-compassion, identity healing, emotional validation',
    child: 'Use gentle, age-appropriate language and imagery'
  },

  // Performance metrics for inner child content
  metrics: {
    trackingGoals: ['Pin saves (expect high save rate)', 'Click-through to resources', 'Board follows', 'Profile engagement'],
    expectedResults: 'Inner child healing content typically sees 20-30% higher engagement and saves than general mental health content',
    bestPerformingTypes: ['Affirmations', 'Meditation guides', 'Journal prompts', 'Healing exercises']
  },

  // Sensitive content guidelines
  contentGuidelines: {
    tone: 'Gentle, nurturing, non-triggering',
    avoid: ['Harsh imagery', 'Aggressive language', 'Triggering content'],
    include: ['Hope', 'Compassion', 'Safety', 'Unconditional love'],
    cta: 'Invite gentle engagement - "Take what serves you", "Be gentle with yourself"'
  },

  timestamp: new Date().toISOString(),
  contentType: contentType,
  ageGroup: ageGroup,
  optimizedFor: 'Pinterest inner child healing niche',
  specialization: 'Childhood trauma recovery and emotional healing'
};

return [{ json: innerChildPinterestOptimized }];
